mapper.select:

select 

from DT_WORK_TIME
where USER_ID = #{userId,jdbcType=CHAR}
  and CALENDAR_YY = #{calendarYy,jdbcType=INTEGER}
  and CALENDAR_MM = #{calendarMm,jdbcType=INTEGER}
  and CALENDAR_DD = #{calendarDd,jdbcType=INTEGER}

mapper.delete:

delete from DT_WORK_TIME
where USER_ID = #{userId,jdbcType=CHAR}
  and CALENDAR_YY = #{calendarYy,jdbcType=INTEGER}
  and CALENDAR_MM = #{calendarMm,jdbcType=INTEGER}
  and CALENDAR_DD = #{calendarDd,jdbcType=INTEGER}
